query ForSaleHubAndSpokeHDPAboveTheFold($zpid: ID!, $supportedExperiences: [BuyAbilityOnHDPExperience!]) {
  property(zpid: $zpid) {
    state
    affordabilityEstimate {
      totalMonthlyCost
      __typename
    }
    ...BuyAbilityFragment
    __typename
  }
  abTests {
    HDP_BUYABILITY_V2_WEB: abTest(trial: "HDP_BUYABILITY_V2_WEB")
    HDP_WEB_OFFER_INSIGHTS_V2: abTest(trial: "HDP_WEB_OFFER_INSIGHTS_V2")
    HDP_SHOP_WITH_BUDGET_WEB: abTest(trial: "HDP_SHOP_WITH_BUDGET_WEB")
    HDP_BUYABILITY_ACTIONABLE_NEGATIVE_OUTCOME_WEB: abTest(
      trial: "HDP_BUYABILITY_ACTIONABLE_NEGATIVE_OUTCOME_WEB"
    )
    __typename
  }
}

fragment BuyAbilityHubFragment on BuyAbilityEnrolledHub {
  header
  zhlAttributionText
  hubLabel
  bodyText
  legalText
  ctaButton {
    ctaCopyText
    enrollmentStatus
    __typename
  }
  affordabilityDetails {
    displayType
    label
    status
    __typename
  }
  __typename
}

fragment BuyAbilityUnEnrolledFragment on BuyAbilityUnenrolled {
  header
  zhlAttributionText
  bodyText
  legalText
  ctaButton {
    ctaCopyText
    enrollmentStatus
    __typename
  }
  __typename
}

fragment BuyAbilitySpokeFragment on BuyAbilityEnrolledSpoke {
  header
  zhlAttributionText
  bodyText
  legalText
  hasNegativeOutcome
  paymentDetails {
    label
    value
    __typename
  }
  ctaButton {
    ctaCopyText
    enrollmentStatus
    __typename
  }
  __typename
}

fragment BuyAbilityMetadataFields on BuyAbilityMetadata {
  targetPrice
  targetMonthlyPayment
  maxPrice
  maxMonthlyPayment
  hasNegativeOutcome
  __typename
}

fragment BuyAbilityFragment on Property {
  buyAbilityData(supportedExperiences: $supportedExperiences) {
    chipLabel
    hub {
      ...BuyAbilityHubFragment
      ...BuyAbilityUnEnrolledFragment
      __typename
    }
    spoke {
      ...BuyAbilitySpokeFragment
      ...BuyAbilityUnEnrolledFragment
      __typename
    }
    buyAbilityMetadata {
      ...BuyAbilityMetadataFields
      __typename
    }
    __typename
  }
  __typename
}
